export default class Query {
    table: string;
    columns: string;
    values: Array<any>;
    conditions: Array<any>;
    action: string;
    static $(frags?: any, ...values: any[]): any[];
    from(table: any): this;
    into(table: any): this;
    where(...args: any): this;
    select(...columns: any[]): this;
    insert(data: any): this;
    evaluate(): any[] | "";
}
